import React from 'react';
import './UserInput.css'

class UserInput extends React.Component 
{
    constructor(props)
    {
        super(props);
        this.state = {
            userTopic : null,
            userCaptions : null,
            userHashtags : null,
        }
        this.handleImageChange = this.handleImageChange.bind(this);
    }    
    topicInput(input){
        this.setState({userTopic : input.target.value})
    }

    captionInput(input){
        this.setState({userCaptions : input.target.value})
    }

    hashtagInput(input){
        this.setState({userHashtags : input.target.value})
    }

    // This function listens to the same port that Flask is running on to get the JSON information from OpenAI API as soon as the user clicks the generate button
    async generateButton()
    {
        try 
        {
            handleImageChange(event) 
            {   
                event.preventDefault();
                const selectedImage = event.target.files[0];

                if(selectedImage)
                {
                    const imageUrl = URL.createObjectURL(selectedImage);
                    this.props.setImage(imageUrl);
                }
            }
        } catch (error) {
            console.log(error);
        }
    }

    async generateButton(){
        try {
            const response = await fetch('http://localhost:5000/generate', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    topic: this.state.userTopic,
                    userHashtags: this.state.userHashtags
                })
            });
            if(response.ok) 
            {
                const data = await response.json();
                this.props.setCaption(data.title); // This is the title generated by the OpenAI API

                // Combine user-provided hashtags with the ones generated by the OpenAI API
                const combinedHashtags = `${this.state.userHashtags} ${data.hashtags}`;
                this.props.setTags(combinedHashtags); // Combined hashtags
            } 
            else 
            {
                console.error("Error generating because: ", response.status); 
            }
        } catch (error) 
        {
            console.error("ERROR: ", error);
        }
    }
      
    render(){
        return(
        <div className="fixed-banner">
            <input onChange={(input)=>{this.topicInput(input)}} type="text" placeholder="Type your post topic or description: (a boat on the coast) ..." className="input-style"/>
            <input onChange={(input)=>{this.captionInput(input)}} type="text" placeholder="(Optional) Enter the link to a previous Instagram Post..." className="optional-style"/>
            <input onChange={(input)=>{this.hashtagInput(input)}} type="text" placeholder="Type guaranteed hashtags..." className="shortInput-style"/> 

            <label class="upload-button">
                <input type="file" accept="image/*" onChange={this.handleImageChange}/> Upload
            </label>

            {this.props.userHashtags}
            <button onClick={()=>{
                this.generateButton(); 
                this.props.setCaption(this.state.userTopic);
                this.props.setTags(this.state.confinedUserHashtags);
            }} className="generate-button">Generate</button>
        </div>
        )
    }
}

export default UserInput;
